        [Test]
        public void DeserializeGithubIssue_AssignCorrectValues() 
        {
            HttpResponseMessage response = new HttpResponseMessage()
            {
                Content = new StringContent(githubIssue, Encoding.UTF8, "application/vnd.github+json")
            };

            var issue = ResponseExtensions.DeserializeGithubIssue(response);

            Assert.IsNotNull(issue);
            Assert.That(issue.Id == 2500114729);
            Assert.That(issue.Title == "1st test issue");
            Assert.That(issue.Body == "dfgsfdgfgdsfgfdgf");
            Assert.IsNull(issue.Description);
        }

        [Test]
        public void DeserializeGithubIssues_AssignCorrectValues()
        {
            HttpResponseMessage response = new HttpResponseMessage()
            {
                Content = new StringContent(githubIssues, Encoding.UTF8, "application/vnd.github+json")
            };

            var issues = ResponseExtensions.DeserializeGithubIssues(response).ToList();

            Assert.IsNotNull(issues);
            Assert.That(issues.Count == 2);

            var issue = issues[0];
            Assert.That(issue.Id == 2500114729);
            Assert.That(issue.Title == "1st test issue");
            Assert.That(issue.Body == "Lorem ipsum dfhggsdhg");
            Assert.IsNull(issue.Description);

            var issue2 = issues[1];
            Assert.That(issue2.Id == 2499694640);
            Assert.That(issue2.Title == "2nd test issue");
            Assert.That(issue2.Body == "dfgsfdgfgdsfgfdgf");
            Assert.IsNull(issue2.Description);
        }

